import React, { useState } from "react";
import styles from "./NavBar.module.css"

const NavBar = () => {
    const [active, setActive] = useState(styles.nav__menu);
    const [toggleIcon, setToggleIcon] = useState(styles.nav__toggler);

    const navHandler = () => {
        active === styles.nav__menu ? setActive( `${styles.nav__menu} ${styles.nav__active}` ) : setActive(styles.nav__menu);
        
        //Toggler

        toggleIcon === styles.nav__toggler ? setToggleIcon(`${styles.nav__toggler} ${styles.toggle}`) : setToggleIcon(styles.nav__toggler);
    }
  return (
    <nav className={styles.nav}>
      <a href="#" className={styles.name}>
        {"<RoelDev />"}
      </a>
      <ul className={active}>
        <li className="nav__item">
          <a href="#" className="nav__link">
            About
          </a>
        </li>
        <li className="nav__item">
          <a href="#" className="nav__link">
            Resume
          </a>
        </li>
        <li className="nav__item">
          <a href="#" className="nav__link">
            Skills
          </a>
        </li>
        <li className="nav__item">
          <a href="#" className="nav__link">
            Projects
          </a>
        </li>
      </ul>
      <div onClick={navHandler} className={toggleIcon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
};

export default NavBar;
