import React, { useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import NavBar from "./components/navBar/NavBar";
import Welcome from "./components/welcome/Welcome";
import About from "./components/about/About";
import Projects from "./components/projects/Projects";
import Skills from "./components/skills/Skills";

function App() {

  const about = useRef(null);
  const projects = useRef(null);
  const skills = useRef(null);

  const ref = {aboutRef: about, projectsRef: projects, skillsRef: skills}

  // const ref = {ref1: about, ref2: projects, ref3: skills};
  return (
    <>
      {/* <Welcome about={scrollToSection(about)} projects={scrollToSection(projects)} skills={scrollToSection(skills)} /> */}
      <Router>
        <Routes>
          {/* <Route exact path={['/', '/#about', '/#projects', '/#skills']}> */}
          <Route exact path={'/'} element={<Welcome ref={ref} />} />
          <Route exact path={'/#about'} element={<About ref={ref} />} />            
          <Route exact path={'/#projects'} element={<Projects ref={ref} />} />
          <Route exact path={'/#skills'} element={<Skills ref={ref} />} />   
        </Routes>  
      </Router>
    </>
  );
}

export default App;
