import React, { useState } from "react";
import styles from "./NavBar.module.css";

const NavBar = React.forwardRef((props,ref) => {
  const [active, setActive] = useState(styles.nav__menu);

  const scrollToSection = (elementRef) => {
    window.scrollTo({
      top: elementRef.current.offsetTop,
      behavior: "smooth",
    });
  };

  const navHandler = () => {
    active === styles.nav__menu
      ? setActive(`${styles.nav__menu} ${styles.nav__active}`)
      : setActive(styles.nav__menu);
  };
  return (
    <nav className={styles.nav}>
      <a href="/#" className={styles.name}>
        {"<RoelDev />"}
      </a>
      <ul className={active}>
        <li className={styles.nav__item} onClick={props.about}>
          <a href="/#" className="nav__link">
            About
          </a>
        </li>
        <li className={styles.nav__item} >
          <a href="/#" className="nav__link">
            Resume
          </a>
        </li>
        <li className={styles.nav__item} onClick={props.skills} >
          <a href="/#" className="nav__link">
            Skills
          </a>
        </li>
        <li className={styles.nav__item} onClick={props.projects} >
          <a href="/#" className="nav__link">
            Projects
          </a>
        </li>
      </ul>
      <div onClick={navHandler} className={styles.nav__toggler}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
});

export default NavBar;
