import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "./NavBar.module.css";

const NavBar = ({about, projects, skills}) => {
  const [active, setActive] = useState(styles.nav__menu);
  // const {ref1,ref2,ref3} = ref;
  
  // const scrollToSection = (elementRef) => {
  //   window.scrollTo({
  //     top: elementRef.current.offsetTop,
  //     behavior: "smooth",
  //   });
  // };

  const handleScroll = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      left: 0,
      behavior: "smooth",
    });
  };

  const navHandler = () => {
    active === styles.nav__menu
      ? setActive(`${styles.nav__menu} ${styles.nav__active}`)
      : setActive(styles.nav__menu);
  };
  return (
    <nav className={styles.nav}>
      <a href="/#" className={styles.name}>
        {"<RoelDev />"}
      </a>
      <ul className={active}>
        <li className={styles.nav__item} >
          <Link to="/#about" onClick={() => {handleScroll(about);}}>
            About
          </Link>
        </li>
        <li className={styles.nav__item} >
          <a href="/#" className="nav__link">
            Resume
          </a>
        </li>
        <li className={styles.nav__item} >
          <Link to="/#skills" onClick={() => {handleScroll(skills);}}>
            Skills
          </Link>
        </li>
        <li className={styles.nav__item}>
          <Link to="/#projects" onClick={() => {handleScroll(projects);}}>
            Projects
          </Link>
        </li>
      </ul>
      <div onClick={navHandler} className={styles.nav__toggler}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
};

export default NavBar;
